UPDATE public.menu_items SET description = 'Дзен-дзен-дзен! Так звенят новогодние колокольчики в Японии. Собрали в один сет всё, чтобы поймать умиротворение перед праздниками.Санта — 1 (6 шт.), Снегопад — 1 (6 шт.), Мишура — 1 (6 шт.), Прайм — 1 (6 шт.), Филадельфия — 1 (6 шт.)' WHERE title = 'Джингл сет';
